function absoluteHero(){var e=$(".absoluteHero");$(window).width()>=768?e.css("margin-top",-1*(e.height()/2)):e.css("margin-top","0px")}$(document).ready(function(){$(document).foundation();absoluteHero();$(".mailchimp-form").on("submit",function(e){e.preventDefault();var t=$(this);$.get("/mailchimp_signup",{email:t.find(".email").val(),fname:t.find(".fname").val(),lname:t.find(".lname").val(),ajax:!0},function(e){if(e=="Success"){t.find(".email").val("").attr("disabled","disabled");t.find("button").fadeOut(250);t.prev("small").text("You're awesome, thanks!")}})})});$(window).resize(function(){absoluteHero()});